import { useState } from 'react';
import { useLocation } from 'wouter';
import { useMutation } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { Card, CardContent } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Eye, EyeOff } from 'lucide-react';
import type { LoginData, InsertUser } from '@shared/schema';

// Recovery form component
function ForgotPasswordForm({ onClose }: { onClose: () => void }) {
  const [step, setStep] = useState<'email' | 'otp' | 'password'>('email');
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const { toast } = useToast();

  const sendOtpMutation = useMutation({
    mutationFn: async (email: string) => {
      const response = await apiRequest('POST', '/api/auth/forgot-password', { email });
      return response.json();
    },
    onSuccess: () => {
      setStep('otp');
      toast({
        title: 'OTP Sent',
        description: 'Please check your email for the verification code.',
      });
    },
    onError: (error) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const verifyOtpMutation = useMutation({
    mutationFn: async (data: { email: string; otp: string }) => {
      const response = await apiRequest('POST', '/api/auth/verify-otp', data);
      return response.json();
    },
    onSuccess: () => {
      setStep('password');
      toast({
        title: 'OTP Verified',
        description: 'Please enter your new password.',
      });
    },
    onError: (error) => {
      toast({
        title: 'Invalid OTP',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: async (data: { email: string; otp: string; newPassword: string }) => {
      const response = await apiRequest('POST', '/api/auth/reset-password', data);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: 'Password Reset Successful',
        description: 'Your password has been updated. Please sign in with your new password.',
      });
      onClose();
    },
    onError: (error) => {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      });
    },
  });

  const handleSendOtp = (e: React.FormEvent) => {
    e.preventDefault();
    if (!email.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter your email address.',
        variant: 'destructive',
      });
      return;
    }
    sendOtpMutation.mutate(email);
  };

  const handleVerifyOtp = (e: React.FormEvent) => {
    e.preventDefault();
    if (!otp.trim()) {
      toast({
        title: 'Error',
        description: 'Please enter the OTP.',
        variant: 'destructive',
      });
      return;
    }
    verifyOtpMutation.mutate({ email, otp });
  };

  const handleResetPassword = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPassword || !confirmPassword) {
      toast({
        title: 'Error',
        description: 'Please fill in all password fields.',
        variant: 'destructive',
      });
      return;
    }
    if (newPassword !== confirmPassword) {
      toast({
        title: 'Error',
        description: 'Passwords do not match.',
        variant: 'destructive',
      });
      return;
    }
    if (newPassword.length < 6) {
      toast({
        title: 'Error',
        description: 'Password must be at least 6 characters long.',
        variant: 'destructive',
      });
      return;
    }
    resetPasswordMutation.mutate({ email, otp, newPassword });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <Card className="w-full max-w-md">
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold">Password Recovery</h2>
            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
              data-testid="close-recovery"
            >
              √ó
            </Button>
          </div>

          {step === 'email' && (
            <form onSubmit={handleSendOtp} className="space-y-4">
              <div>
                <Label htmlFor="recovery-email">Email Address</Label>
                <Input
                  id="recovery-email"
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  data-testid="recovery-email-input"
                />
              </div>
              <Button
                type="submit"
                className="w-full"
                disabled={sendOtpMutation.isPending}
                data-testid="send-otp-button"
              >
                {sendOtpMutation.isPending ? 'Sending...' : 'Send OTP'}
              </Button>
            </form>
          )}

          {step === 'otp' && (
            <form onSubmit={handleVerifyOtp} className="space-y-4">
              <div>
                <Label htmlFor="otp">Enter OTP</Label>
                <Input
                  id="otp"
                  type="text"
                  placeholder="Enter 6-digit code"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                  maxLength={6}
                  required
                  data-testid="otp-input"
                />
                <p className="text-sm text-gray-600 mt-1">
                  OTP sent to {email}
                </p>
              </div>
              <Button
                type="submit"
                className="w-full"
                disabled={verifyOtpMutation.isPending}
                data-testid="verify-otp-button"
              >
                {verifyOtpMutation.isPending ? 'Verifying...' : 'Verify OTP'}
              </Button>
            </form>
          )}

          {step === 'password' && (
            <form onSubmit={handleResetPassword} className="space-y-4">
              <div>
                <Label htmlFor="new-password">New Password</Label>
                <Input
                  id="new-password"
                  type="password"
                  placeholder="Enter new password"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  required
                  data-testid="new-password-input"
                />
              </div>
              <div>
                <Label htmlFor="confirm-password">Confirm Password</Label>
                <Input
                  id="confirm-password"
                  type="password"
                  placeholder="Confirm new password"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  required
                  data-testid="confirm-password-input"
                />
              </div>
              <Button
                type="submit"
                className="w-full"
                disabled={resetPasswordMutation.isPending}
                data-testid="reset-password-button"
              >
                {resetPasswordMutation.isPending ? 'Updating...' : 'Change Password'}
              </Button>
            </form>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

export default function Login() {
  const [, navigate] = useLocation();
  const [activeTab, setActiveTab] = useState('signin');
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [loginError, setLoginError] = useState('');
  const [signupError, setSignupError] = useState('');

  // Login form state
  const [loginData, setLoginData] = useState<LoginData>({
    email: '',
    password: '',
  });
  const [showLoginPassword, setShowLoginPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);

  // Signup form state
  const [signupData, setSignupData] = useState<InsertUser>({
    fullName: '',
    email: '',
    phone: '',
    password: '',
  });
  const [showSignupPassword, setShowSignupPassword] = useState(false);

  const { toast } = useToast();

  const loginMutation = useMutation({
    mutationFn: async (data: LoginData) => {
      const response = await apiRequest('POST', '/api/auth/login', data);
      return response.json();
    },
    onSuccess: (user) => {
      localStorage.setItem('user', JSON.stringify(user));
      setLoginError('');
      toast({
        title: 'Login successful',
        description: 'Welcome back! Redirecting to jobs page...',
      });
      navigate('/jobs');
    },
    onError: (error) => {
      setLoginError('Wrong username or wrong password');
    },
  });

  const signupMutation = useMutation({
    mutationFn: async (data: InsertUser) => {
      const response = await apiRequest('POST', '/api/auth/register', data);
      return response.json();
    },
    onSuccess: (user) => {
      localStorage.setItem('user', JSON.stringify(user));
      setSignupError('');
      toast({
        title: 'Account created successfully',
        description: 'Welcome! Redirecting to jobs page...',
      });
      navigate('/jobs');
    },
    onError: (error) => {
      setSignupError(error.message);
    },
  });

  const handleLoginSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setLoginError('');
    loginMutation.mutate(loginData);
  };

  const handleSignupSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setSignupError('');
    signupMutation.mutate(signupData);
  };

  const handleLoginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setLoginData({
      ...loginData,
      [e.target.name]: e.target.value,
    });
    if (loginError) setLoginError('');
  };

  const handleSignupChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSignupData({
      ...signupData,
      [e.target.name]: e.target.value,
    });
    if (signupError) setSignupError('');
  };

  return (
    <div className="min-h-screen flex">
      {/* Left side - Hero image */}
      <div className="hidden lg:flex lg:w-1/2 relative">
        <div 
          className="w-full bg-cover bg-center"
          style={{
            backgroundImage: `linear-gradient(45deg, rgba(99, 102, 241, 0.8), rgba(139, 92, 246, 0.8)), url('data:image/svg+xml;base64,')`
          }}
        >
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="text-center text-white p-8">
              <h1 className="text-4xl lg:text-5xl font-bold mb-4">
                Smart Job Portal Made Simple
              </h1>
              <p className="text-lg lg:text-xl text-blue-100 max-w-md">
                Find, reserve, and apply for job opportunities in seconds. Join thousands of satisfied users.
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Right side - Authentication form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center bg-gray-50 p-4 sm:p-6 lg:p-8">
        <div className="w-full max-w-md">
          {/* Development notice */}
          <div className="mb-4 sm:mb-6 p-3 bg-blue-50 border border-blue-200 rounded-lg">
            <p className="text-xs sm:text-sm text-blue-700 text-center">
              üîß Development Mode: Login/Signup page always shows for testing
            </p>
          </div>

          <Card className="shadow-lg border-0">
            <CardContent className="p-4 sm:p-6">
              <Tabs value={activeTab} onValueChange={setActiveTab}>
                <TabsList className="grid w-full grid-cols-2 mb-6">
                  <TabsTrigger value="signin" data-testid="signin-tab">Sign In</TabsTrigger>
                  <TabsTrigger value="signup" data-testid="signup-tab">Sign Up</TabsTrigger>
                </TabsList>

                {/* Sign In Tab */}
                <TabsContent value="signin">
                  <form onSubmit={handleLoginSubmit} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="login-email" className="text-red-600">Email or Phone *</Label>
                      <Input
                        id="login-email"
                        name="email"
                        type="email"
                        placeholder=""
                        value={loginData.email}
                        onChange={handleLoginChange}
                        required
                        data-testid="login-email-input"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="login-password" className="text-red-600">Password *</Label>
                      <div className="relative">
                        <Input
                          id="login-password"
                          name="password"
                          type={showLoginPassword ? 'text' : 'password'}
                          placeholder=""
                          value={loginData.password}
                          onChange={handleLoginChange}
                          required
                          data-testid="login-password-input"
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowLoginPassword(!showLoginPassword)}
                          data-testid="toggle-login-password"
                        >
                          {showLoginPassword ? (
                            <EyeOff className="h-4 w-4 text-gray-400" />
                          ) : (
                            <Eye className="h-4 w-4 text-gray-400" />
                          )}
                        </Button>
                      </div>
                    </div>

                    {/* Error message */}
                    {loginError && (
                      <p className="text-red-500 text-sm" data-testid="login-error">
                        {loginError}
                      </p>
                    )}

                    {/* Remember me checkbox */}
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          id="remember-me"
                          checked={rememberMe}
                          onCheckedChange={(checked) => setRememberMe(checked as boolean)}
                          data-testid="remember-me-checkbox"
                        />
                        <Label htmlFor="remember-me" className="text-sm">
                          Remember me
                        </Label>
                      </div>
                      <Button
                        type="button"
                        variant="link"
                        className="p-0 h-auto text-blue-600 text-sm"
                        onClick={() => setShowForgotPassword(true)}
                        data-testid="forgot-password-link"
                      >
                        Forgot password?
                      </Button>
                    </div>

                    <Button
                      type="submit"
                      className="w-full"
                      disabled={loginMutation.isPending}
                      data-testid="signin-button"
                    >
                      {loginMutation.isPending ? 'Signing in...' : 'Sign In'}
                    </Button>
                  </form>
                </TabsContent>

                {/* Sign Up Tab */}
                <TabsContent value="signup">
                  <form onSubmit={handleSignupSubmit} className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="signup-name">Full Name *</Label>
                      <Input
                        id="signup-name"
                        name="fullName"
                        type="text"
                        placeholder="Enter your full name"
                        value={signupData.fullName}
                        onChange={handleSignupChange}
                        required
                        data-testid="signup-name-input"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="signup-email">Email *</Label>
                      <Input
                        id="signup-email"
                        name="email"
                        type="email"
                        placeholder="Enter your email"
                        value={signupData.email}
                        onChange={handleSignupChange}
                        required
                        data-testid="signup-email-input"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="signup-phone">Phone Number *</Label>
                      <Input
                        id="signup-phone"
                        name="phone"
                        type="tel"
                        placeholder="Enter your phone number"
                        value={signupData.phone || ''}
                        onChange={handleSignupChange}
                        required
                        data-testid="signup-phone-input"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="signup-password">Password *</Label>
                      <div className="relative">
                        <Input
                          id="signup-password"
                          name="password"
                          type={showSignupPassword ? 'text' : 'password'}
                          placeholder="Create a password"
                          value={signupData.password}
                          onChange={handleSignupChange}
                          required
                          data-testid="signup-password-input"
                        />
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                          onClick={() => setShowSignupPassword(!showSignupPassword)}
                          data-testid="toggle-signup-password"
                        >
                          {showSignupPassword ? (
                            <EyeOff className="h-4 w-4 text-gray-400" />
                          ) : (
                            <Eye className="h-4 w-4 text-gray-400" />
                          )}
                        </Button>
                      </div>
                    </div>

                    {/* Error message */}
                    {signupError && (
                      <p className="text-red-500 text-sm" data-testid="signup-error">
                        {signupError}
                      </p>
                    )}

                    <Button
                      type="submit"
                      className="w-full"
                      disabled={signupMutation.isPending}
                      data-testid="signup-button"
                    >
                      {signupMutation.isPending ? 'Creating account...' : 'Sign Up'}
                    </Button>
                  </form>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </div>
      </div>

      {/* Forgot Password Modal */}
      {showForgotPassword && (
        <ForgotPasswordForm onClose={() => setShowForgotPassword(false)} />
      )}
    </div>
  );
}

client/src/pages/my-applications.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';
import { Navbar } from '@/components/job-portal/navbar';
import {
  Briefcase,
  MapPin,
  Clock,
  Building,
  Trash2,
  ExternalLink,
  Calendar,
  CheckCircle
} from 'lucide-react';

export default function MyApplications() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [user, setUser] = useState<any>({});

  // Check if user is logged in
  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (!userData) {
      navigate('/login');
      return;
    }
    setUser(JSON.parse(userData));
  }, [navigate]);

  const { data: applications = [], isLoading } = useQuery({
    queryKey: ['applications/user', user.id],
    queryFn: async () => {
      if (!user?.id) return [];
      const response = await apiRequest('GET', `/api/applications/user/${user.id}`);
      if (!response.ok) {
        throw new Error('Failed to fetch applications');
      }
      return response.json();
    },
    enabled: !!user.id,
    staleTime: 0,
    retry: 3,
  });

  const { data: jobs = [] } = useQuery({
    queryKey: ['jobs'],
    queryFn: async () => {
      const response = await fetch('/api/jobs');
      if (!response.ok) {
        throw new Error('Failed to fetch jobs');
      }
      return response.json();
    },
  });

  const removeApplicationMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('DELETE', `/api/applications/${applicationId}`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['applications/user', user.id] });
      toast({
        title: 'Application removed',
        description: 'Your job application has been removed successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Remove failed',
        description: error.message || 'Failed to remove application',
        variant: 'destructive',
      });
    },
  });

  const softDeleteMutation = useMutation({
    mutationFn: async (applicationId: string) => {
      const response = await apiRequest('POST', `/api/applications/${applicationId}/soft-delete`);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['applications/user', user.id] });
      queryClient.invalidateQueries({ queryKey: ['deleted-posts/user', user.id] });
      toast({
        title: 'Post deleted',
        description: 'Your job application has been moved to deleted posts. It can be restored within 5 days.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Delete failed',
        description: error.message || 'Failed to delete post',
        variant: 'destructive',
      });
    },
  });

  // Get job details for each application
  const applicationsWithJobs = Array.isArray(applications) ? applications.map((app: any) => {
    const job = Array.isArray(jobs) ? jobs.find((j: any) => j.id === app.jobId) : null;
    return { ...app, job };
  }).filter(app => app.job) : [];

  const handleRemoveApplication = (applicationId: string) => {
    if (window.confirm('Are you sure you want to remove this application?')) {
      removeApplicationMutation.mutate(applicationId);
    }
  };

  const handleSoftDelete = (applicationId: string) => {
    if (window.confirm('Are you sure you want to delete this post? It will be moved to deleted posts and can be restored within 5 days.')) {
      softDeleteMutation.mutate(applicationId);
    }
  };

  const handleViewJob = (jobId: string) => {
    navigate(`/jobs/${jobId}`);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navbar />
        <div className="flex items-center justify-center h-96">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading your applications...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="max-w-6xl mx-auto px-3 sm:px-4 md:px-6 lg:px-8 py-4 sm:py-6 md:py-8">
        {/* Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">My Applications</h1>
              <p className="text-gray-600 mt-2">Track and manage your job applications</p>
            </div>
            <div className="text-right">
              <div className="text-4xl font-bold text-blue-600">{applicationsWithJobs.length}</div>
              <div className="text-sm text-gray-600 font-medium">Total Applications</div>
            </div>
          </div>
        </div>

        <div className="space-y-6">
          {applicationsWithJobs.length === 0 ? (
            <Card>
              <CardContent className="p-12 text-center">
                <div className="text-gray-400 mb-4">
                  <Briefcase className="w-16 h-16 mx-auto" />
                </div>
                <h3 className="text-xl font-medium text-gray-900 mb-2">No Applications Yet</h3>
                <p className="text-gray-600 mb-6">
                  You haven't applied to any jobs yet. Start exploring opportunities and apply to jobs that match your skills.
                </p>
                <Button onClick={() => navigate('/jobs')} data-testid="browse-jobs-button">
                  <Briefcase className="w-4 h-4 mr-2" />
                  Browse Jobs
                </Button>
              </CardContent>
            </Card>
          ) : (
            applicationsWithJobs.map((application: any) => {
              const { job } = application;
              const appliedDate = new Date(application.createdAt || application.appliedAt);
              const isExpired = new Date(job.closingDate) < new Date();

              return (
                <Card
                  key={application.id}
                  className="w-full hover:shadow-md transition-shadow"
                  data-testid={`application-card-${application.id}`}
                >
                  <CardContent className="p-4 sm:p-5 md:p-6">
                    {/* Header with Title and Applied Badge */}
                    <div className="flex flex-col sm:flex-row sm:items-start justify-between mb-4 gap-2 sm:gap-0">
                      <div className="flex-1">
                        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-2">
                          {job.title}
                        </h3>
                        <div className="flex flex-col sm:flex-row sm:items-center gap-2 sm:gap-4 text-xs sm:text-sm text-gray-600 mb-3">
                          <div className="flex items-center gap-1">
                            <Building className="w-4 h-4" />
                            <span>{job.company.name}</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <MapPin className="w-4 h-4" />
                            <span>{job.location}</span>
                          </div>
                          <div className="flex items-center gap-1">
                            <Calendar className="w-4 h-4" />
                            <span>Applied: {appliedDate.toLocaleDateString('en-GB')}</span>
                          </div>
                        </div>
                      </div>
                      <Badge className="bg-green-500 text-white px-2 sm:px-3 py-1 text-xs sm:text-sm self-start sm:self-auto">
                        Applied
                      </Badge>
                    </div>

                    {/* Salary and Closing Date */}
                    <div className="flex flex-col sm:flex-row sm:items-center gap-2 sm:gap-6 mb-4 text-xs sm:text-sm">
                      <span className="font-semibold text-green-600">{job.salary}</span>
                      <div className="flex items-center gap-1 text-gray-600">
                        <Clock className="w-4 h-4" />
                        <span>Closes: {new Date(job.closingDate).toLocaleDateString('en-GB')}</span>
                      </div>
                      {isExpired && (
                        <Badge variant="destructive" className="text-xs px-2 py-1">Expired</Badge>
                      )}
                    </div>

                    {/* Job Description */}
                    <p className="text-gray-700 text-xs sm:text-sm mb-4 leading-relaxed">
                      {job.description.length > 150
                        ? `${job.description.substring(0, 150)}...`
                        : job.description
                      }
                    </p>

                    {/* Skills */}
                    <div className="flex flex-wrap gap-1.5 sm:gap-2 mb-4 sm:mb-6">
                      {job.skills.split(',').slice(0, 6).map((skill: string, index: number) => (
                        <Badge key={index} variant="outline" className="text-xs px-2 py-1 bg-blue-50 text-blue-700 border-blue-200">
                          {skill.trim()}
                        </Badge>
                      ))}
                      {job.skills.split(',').length > 6 && (
                        <Badge variant="outline" className="text-xs px-2 py-1 bg-gray-50 text-gray-600 border-gray-200">
                          +{job.skills.split(',').length - 6}
                        </Badge>
                      )}
                    </div>

                    {/* Bottom Section - Status and Actions */}
                    <div className="flex flex-col sm:flex-row sm:items-center justify-between pt-3 sm:pt-4 border-t border-gray-100 gap-3 sm:gap-0">
                      <div className="text-xs sm:text-sm text-gray-600">
                        Application Status: <span className="text-green-600 font-medium">Submitted</span>
                      </div>

                      <div className="flex flex-col sm:flex-row gap-2 w-full sm:w-auto">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleViewJob(job.id)}
                          data-testid={`view-job-${application.id}`}
                          className="text-xs h-8 w-full sm:w-auto"
                        >
                          <ExternalLink className="w-3 h-3 mr-1" />
                          View Job
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleSoftDelete(application.id)}
                          disabled={softDeleteMutation.isPending}
                          data-testid={`delete-post-${application.id}`}
                          className="text-xs h-8 w-full sm:w-auto text-orange-600 border-orange-300 hover:bg-orange-50"
                        >
                          <Trash2 className="w-3 h-3 mr-1" />
                          Delete Post
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

